'use strict'

const NutrientDataLaboratory = require('../')

describe('NutrientDataLaboratory', () => {
  describe('#foodReports()', () => {
    it('should request a food report for cheddar cheese', (done) => {
      let ndl = new NutrientDataLaboratory('DEMO_KEY')

      ndl.foodReports({
        ndbno: '01009',
        type: 'b'
      }, done)
    })
  })

  describe('#lists()', () => {
    it('should request a list of various foods', (done) => {
      let ndl = new NutrientDataLaboratory('DEMO_KEY')

      ndl.lists({
        lt: 'f',
        sort: 'n'
      }, done)
    })
  })

  describe('#nutrientReports()', () => {
    it('should request nutrient reports for various nutrients', (done) => {
      let ndl = new NutrientDataLaboratory('DEMO_KEY')

      ndl.nutrientReports({
        nutrients: ['205', '204', '208', '269']
      }, done)
    })
  })

  describe('#search()', () => {
    it('should search for \'butter\'', (done) => {
      let ndl = new NutrientDataLaboratory('DEMO_KEY')

      ndl.search({
        q: 'butter',
        max: 25,
        offset: 0
      }, done)
    })
  })
})
